import 'package:flutter_test/flutter_test.dart';
import 'package:enigmo_app/services/crypto_engine.dart';
import 'package:enigmo_app/services/key_manager.dart';
import 'package:enigmo_app/models/message.dart';

void main() {
  TestWidgetsFlutterBinding.ensureInitialized();

  group('Security Tests', () {
    late CryptoEngine cryptoEngine;
    late KeyManager keyManager;

    setUp(() {
      cryptoEngine = CryptoEngine();
      keyManager = KeyManager();
    });

    group('Cryptographic Security Tests', () {
      test('should generate cryptographically secure keys', () async {
        final keyPairs = <KeyPair>[];

        // Generate multiple key pairs to test randomness
        for (var i = 0; i < 100; i++) {
          keyPairs.add(await cryptoEngine.generateKeyPair());
        }

        // Check that all public keys are unique
        final publicKeys = keyPairs.map((kp) => kp.publicKey).toSet();
        expect(publicKeys.length, equals(100));

        // Check that all private keys are unique
        final privateKeys = keyPairs.map((kp) => kp.privateKey).toSet();
        expect(privateKeys.length, equals(100));

        // Verify key format (should be hex strings of correct length)
        for (final keyPair in keyPairs) {
          expect(keyPair.publicKey.length, equals(64));
          expect(keyPair.privateKey.length, equals(64));
          expect(int.tryParse(keyPair.publicKey, radix: 16), isNotNull);
          expect(int.tryParse(keyPair.privateKey, radix: 16), isNotNull);
        }
      });

      test('should provide semantic security', () async {
        const message = 'The same message encrypted multiple times';

        // Encrypt the same message multiple times
        final encrypted1 = await cryptoEngine.encrypt(message);
        final encrypted2 = await cryptoEngine.encrypt(message);
        final encrypted3 = await cryptoEngine.encrypt(message);

        // All ciphertexts should be different (semantic security)
        expect(encrypted1, isNot(equals(encrypted2)));
        expect(encrypted2, isNot(equals(encrypted3)));
        expect(encrypted1, isNot(equals(encrypted3)));

        // But all should decrypt to the same plaintext
        final decrypted1 = await cryptoEngine.decrypt(encrypted1);
        final decrypted2 = await cryptoEngine.decrypt(encrypted2);
        final decrypted3 = await cryptoEngine.decrypt(encrypted3);

        expect(decrypted1, equals(message));
        expect(decrypted2, equals(message));
        expect(decrypted3, equals(message));
      });

      test('should resist ciphertext manipulation', () async {
        const message = 'Original message';

        final encrypted = await cryptoEngine.encrypt(message);

        // Try to manipulate the ciphertext
        final manipulated = encrypted.substring(0, encrypted.length - 1) + 'x';

        // Should fail to decrypt manipulated ciphertext
        expect(
          () async => await cryptoEngine.decrypt(manipulated),
          throwsA(isA<Exception>()),
        );
      });

      test('should validate signature integrity', () async {
        const message = 'Message to sign';
        final keyPair = await cryptoEngine.generateKeyPair();

        final signature = await cryptoEngine.sign(message, keyPair.privateKey);
        final isValid = await cryptoEngine.verify(message, signature, keyPair.publicKey);

        expect(isValid, isTrue);

        // Test with wrong message
        final isValidWrongMessage = await cryptoEngine.verify('Wrong message', signature, keyPair.publicKey);
        expect(isValidWrongMessage, isFalse);

        // Test with wrong key
        final wrongKeyPair = await cryptoEngine.generateKeyPair();
        final isValidWrongKey = await cryptoEngine.verify(message, signature, wrongKeyPair.publicKey);
        expect(isValidWrongKey, isFalse);

        // Test with manipulated signature
        final manipulatedSignature = signature.substring(0, signature.length - 1) + 'x';
        final isValidManipulated = await cryptoEngine.verify(message, manipulatedSignature, keyPair.publicKey);
        expect(isValidManipulated, isFalse);
      });

      test('should handle edge cases securely', () async {
        // Test with empty strings
        const emptyMessage = '';
        final encryptedEmpty = await cryptoEngine.encrypt(emptyMessage);
        final decryptedEmpty = await cryptoEngine.decrypt(encryptedEmpty);
        expect(decryptedEmpty, equals(emptyMessage));

        // Test with very long messages
        final longMessage = 'A' * 100000; // 100KB message
        final encryptedLong = await cryptoEngine.encrypt(longMessage);
        final decryptedLong = await cryptoEngine.decrypt(encryptedLong);
        expect(decryptedLong, equals(longMessage));

        // Test with special characters
        const specialMessage = 'Special chars: Ã±Ã¡Ã©Ã­Ã³Ãº ä¸­æ–‡ ðŸš€ ðŸ’¯ \n\t\r\0';
        final encryptedSpecial = await cryptoEngine.encrypt(specialMessage);
        final decryptedSpecial = await cryptoEngine.decrypt(encryptedSpecial);
        expect(decryptedSpecial, equals(specialMessage));
      });
    });

    group('Key Management Security Tests', () {
      test('should securely store and retrieve keys', () async {
        const userId = 'security_test_user';
        final keyPair = await cryptoEngine.generateKeyPair();

        // Store keys
        await keyManager.storeUserId(userId);
        await keyManager.storeKeyPair(userId, keyPair.publicKey, keyPair.privateKey);

        // Retrieve keys
        final storedUserId = await keyManager.getUserId();
        final storedKeyPair = await keyManager.getKeyPair(userId);

        expect(storedUserId, equals(userId));
        expect(storedKeyPair, isNotNull);
        expect(storedKeyPair!.publicKey, equals(keyPair.publicKey));
        expect(storedKeyPair!.privateKey, equals(keyPair.privateKey));
      });

      test('should isolate keys between users', () async {
        const userId1 = 'user1_security';
        const userId2 = 'user2_security';

        final keyPair1 = await cryptoEngine.generateKeyPair();
        final keyPair2 = await cryptoEngine.generateKeyPair();

        // Store different keys for different users
        await keyManager.storeKeyPair(userId1, keyPair1.publicKey, keyPair1.privateKey);
        await keyManager.storeKeyPair(userId2, keyPair2.publicKey, keyPair2.privateKey);

        // Retrieve and verify isolation
        final retrievedPair1 = await keyManager.getKeyPair(userId1);
        final retrievedPair2 = await keyManager.getKeyPair(userId2);

        expect(retrievedPair1!.publicKey, equals(keyPair1.publicKey));
        expect(retrievedPair1!.privateKey, equals(keyPair1.privateKey));
        expect(retrievedPair2!.publicKey, equals(keyPair2.publicKey));
        expect(retrievedPair2!.privateKey, equals(keyPair2.privateKey));

        // Keys should be different between users
        expect(retrievedPair1!.publicKey, isNot(equals(retrievedPair2!.publicKey)));
        expect(retrievedPair1!.privateKey, isNot(equals(retrievedPair2!.privateKey)));
      });

      test('should securely clear session data', () async {
        const userId = 'session_clear_test';
        final keyPair = await cryptoEngine.generateKeyPair();

        // Store data
        await keyManager.storeUserId(userId);
        await keyManager.storeKeyPair(userId, keyPair.publicKey, keyPair.privateKey);

        // Verify data exists
        final storedUserId = await keyManager.getUserId();
        final storedKeyPair = await keyManager.getKeyPair(userId);
        expect(storedUserId, isNotNull);
        expect(storedKeyPair, isNotNull);

        // Clear session
        await keyManager.clearSession();

        // Verify data is cleared
        final clearedUserId = await keyManager.getUserId();
        final clearedKeyPair = await keyManager.getKeyPair(userId);
        expect(clearedUserId, isNull);
        expect(clearedKeyPair, isNull);
      });

      test('should validate user ID format', () async {
        // Valid 16-character hex IDs
        const validIds = [
          '0123456789abcdef',
          'fedcba9876543210',
          'aaaaaaaaaaaaaaaa',
          'ffffffffffffffff',
        ];

        for (final id in validIds) {
          expect(id.length, equals(16));
          expect(int.tryParse(id, radix: 16), isNotNull);
        }

        // Invalid IDs
        const invalidIds = [
          'short',
          '